from collections import defaultdict
from typing import List


def threeSum(nums: List[int]) -> List[List[int]]:
    nums.sort()

    sums_dict = defaultdict(set)

    below_zero_ix = None

    for i in range(len(nums)):
        if nums[i] >= 0 and i > 0:
            below_zero_ix = i - 1
            break

    for i in range(len(nums)):
        if i > 0 and nums[i] == nums[i - 1]:
            continue
        for j in range(len(nums)):
            if j > i + 1 and nums[j] == nums[j - 1]:
                continue
            elif nums[i] > 0 and nums[j] > 0:
                break
            elif nums[i] < 0 and j < below_zero_ix:
                continue
            elif i < j:
                sum_ = nums[i] + nums[j]
                sums_dict[sum_].add((i, j))

    #print(sums_dict)

    results = dict()

    for i, num in enumerate(nums):
        if num * -1 in sums_dict:
            for doublet in sums_dict[num * -1]:
                if i not in doublet:
                    triplet_tuple = tuple(sorted([nums[x] for x in doublet] + [num]))
                    if triplet_tuple not in results:
                        results[triplet_tuple] = list(triplet_tuple)

    return list(results.values())


print(threeSum([-1,0,1,2,-1,-4]), [[-1, 0, 1], [-1, -1, 2]])
print(threeSum([0,1,1]), [])
print(threeSum([0,0,0]), [[0, 0, 0]])
